// This function returns the factorial of a given number using recursion
function factorial(n) {
    if (n === 1) {
        return 1;
    } else {
        return n * factorial(n - 1);
    }
}

const number = 5;
console.log(`The factorial of ${number} is ${factorial(number)}`);

