// This program implements the bubble sort algorithm
const bubbleSort = (arr) => {
    const n = arr.length;
    for (let i = 0; i < n - 1; i++) {
        for (let j = 0; j < n - i - 1; j++) {
            if (arr[j] > arr[j + 1]) {
                const temp = arr[j];
                arr[j] = arr[j + 1];
                arr[j + 1] = temp;
            }
        }
    }
};

const arr = [5, 1, 4, 2, 8];
bubbleSort(arr);
console.log('Sorted array:');
console.log(arr);

