// This function returns the factorial of a given number using recursion
#include <iostream>
using namespace std;

int factorial(int n) {
    if (n == 1) {
        return 1;
    } else {
        return n * factorial(n - 1);
    }
}

int main() {
    int number = 5;
    cout << "The factorial of " << number << " is " << factorial(number) << endl;
    return 0;
}

