// This function returns the factorial of a given number using recursion
#include <stdio.h>

int factorial(int n) {
    if (n == 1) {
        return 1;
    } else {
        return n * factorial(n - 1);
    }
}

int main() {
    int number = 5;
    printf("The factorial of %d is %d\n", number, factorial(number));
    return 0;
}

