// This program calculates the sum of two numbers and prints the result
#include <iostream>

int main() {
    int a = 1; // First number
    int b = 2; // Second number
    int sum = a + b; // Sum of the two numbers
    std::cout << "The sum of " << a << " and " << b << " is " << sum << std::endl; // Print the result
    return 0;
}

// This function returns the factorial of a given number using recursion
#include <iostream>

int factorial(int n) {
    if (n == 1) {
        return 1;
    } else {
        return n * factorial(n - 1);
    }
}

int main() {
    int number = 5;
    std::cout << "The factorial of " << number << " is " << factorial(number) << std::endl;
    return 0;
}
