// This program calculates the sum of two numbers and prints the result
#include <iostream>

int main() {
    int a = 1; // First number
    int b = 2; // Second number
    int sum = a + b; // Sum of the two numbers
    std::cout << "The sum of " << a << " and " << b << " is " << sum << std::endl; // Print the result
    return 0;
}

// This function returns the factorial of a given number using recursion
#include <iostream>

int factorial(int n) {
    if (n == 1) {
        return 1;
    } else {
        return n * factorial(n - 1);
    }
}

int main() {
    int number = 5;
    std::cout << "The factorial of " << number << " is " << factorial(number) << std::endl;
    return 0;
}

// This program calculates the average of three numbers
#include <iostream>

int main() {
    float num1 = 5.5, num2 = 10.5, num3 = 20.0;
    float average = (num1 + num2 + num3) / 3;
    std::cout << "The average of " << num1 << ", " << num2 << ", and " << num3 << " is " << average << std::endl;
    return 0;
}

// This program checks whether a number is even or odd
#include <iostream>

int main() {
    int num = 7;
    if (num % 2 == 0) {
        std::cout << num << " is even" << std::endl;
    } else {
        std::cout << num << " is odd" << std::endl;
    }
    return 0;
}

// This program implements a simple calculator
#include <iostream>

int main() {
    char op;
    double num1, num2;
    std::cout << "Enter an operator (+, -, *, /): ";
    std::cin >> op;
    std::cout << "Enter two operands: ";
    std::cin >> num1 >> num2;

    switch(op) {
        case '+':
            std::cout << num1 << " + " << num2 << " = " << num1 + num2 << std::endl;
            break;
        case '-':
            std::cout << num1 << " - " << num2 << " = " << num1 - num2 << std::endl;
            break;
        case '*':
            std::cout << num1 << " * " << num2 << " = " << num1 * num2 << std::endl;
            break;
        case '/':
            if (num2 != 0.0)
                std::cout << num1 << " / " << num2 << " = " << num1 / num2 << std::endl;
            else
                std::cout << "Division by zero is not allowed." << std::endl;
            break;
        default:
            std::cout << "Invalid operator." << std::endl;
            break;
    }

    return 0;
}

