// This program reverses a given string
#include <iostream>
#include <string>

int main() {
    std::string str;
    std::cout << "Enter a string: ";
    std::getline(std::cin, str);
    int len = str.length();
    for(int i = 0; i < len / 2; i++) {
        std::swap(str[i], str[len - i - 1]);
    }
    std::cout << "Reversed string: " << str << std::endl;
    return 0;
}

// This program finds the largest element in an array
#include <iostream>

int main() {
    int arr[] = {10, 20, 30, 40, 50};
    int max = arr[0];
    for(int i = 1; i < 5; i++) {
        if(arr[i] > max) {
            max = arr[i];
        }
    }
    std::cout << "The largest element in the array is " << max << std::endl;
    return 0;
}

// This program swaps two numbers using a temporary variable
#include <iostream>

int main() {
    int a = 5, b = 10, temp;
    std::cout << "Before swapping: a = " << a << ", b = " << b << std::endl;
    temp = a;
    a = b;
    b = temp;
    std::cout << "After swapping: a = " << a << ", b = " << b << std::endl;
    return 0;
}
